				if (where < end() - 1)
					stt_memmove((uint8_t*) where, (uint8_t*) (where+1), (iend - where)*sizeof(T));
				
				sso.resize_impl(sso.size()-sizeof(T));
				if constexpr (isString()) writeNullTerminator((uint8_t*) end());
				
